	sleep 20

	echo 'Initializing cluster...'
	sudo /opt/couchbase/bin/couchbase-cli cluster-init -c 192.168.50.70:8091 --cluster-init-username=Administrator --cluster-init-password=password --cluster-init-ramsize=1024 -u Administrator -p password

